import React from 'react'

export default function Demo() {
  const [count, setCount] = React.useState(0)
  React.useEffect(() => {
    setInterval(() => {
        add()
    }, 1000)
  }, [])
  //   console.log(count, setCount)
  function add() {
    setCount(count + 1)
  }

  return (
    <div>
      <h2>当前求和为：{count}</h2>
      <button onClick={add}>点我+1</button>
    </div>
  )
}
